class pokemon{constructor(e){this.id=e.id,this.name=e.name,this.sprite=e.sprites.front_default,this.types=e.types.map(e=>e.type.name),this.height=e.height,this.weight=e.weight,this.abilities=e.abilities.map(e=>e.ability.name),this.stats=e.stats.map(e=>e.base_stat),this.moves=e.moves,this.captureRate=null,this.eggGroups=[],this.growthRate="",this.generation="",this.description="",this.evolutionChain=null,this.stats=e.stats.map(e=>({name:e.stat.name,value:e.base_stat}))}getFormattedName(){return this.name.charAt(0).toUpperCase()+this.name.slice(1)}getHeightInMeters(){return this.height/10+" m"}getWeightInKg(){return this.weight/10+" kg"}getTypeString(){return this.types.join(" / ")}getAbilitiesString(){return this.abilities.join(", ")}getEggMoves(){return this.moves.filter(e=>e.version_group_details.some(e=>"egg"===e.move_learn_method.name))}getOtherMoves(){return this.moves.filter(e=>e.version_group_details.every(e=>"egg"!==e.move_learn_method.name))}getDescription(e){e=e.flavor_text_entries.find(e=>"en"===e.language.name);return e?e.flavor_text.replace(/\n|\f/g," "):"Description not available."}setDescriptionFromSpeciesData(e){var t=e.flavor_text_entries.find(e=>"en"===e.language.name);this.description=t?t.flavor_text.replace(/\n|\f/g," "):"Description not available.",this.captureRate=e.capture_rate,this.eggGroups=e.egg_groups.map(e=>e.name),this.growthRate=e.growth_rate.name,this.generation=e.generation.name}setDescriptionFromSpeciesData(e){var t=e.flavor_text_entries.find(e=>"en"===e.language.name);this.description=t?t.flavor_text.replace(/\n|\f/g," "):"Description not available.",this.captureRate=e.capture_rate,this.eggGroups=e.egg_groups.map(e=>e.name),this.growthRate=e.growth_rate.name,this.generation=e.generation.name,this.evolutionUrl=e.evolution_chain.url}setEvolutionChain(e){this.evolutionChain=e}}export{pokemon};