async function fetchPokemonSprite(r){try{var e=await fetch("https://pokeapi.co/api/v2/pokemon/"+r.toLowerCase());if(e.ok)return(await e.json()).sprites.front_default;throw new Error("No se pudo obtener el sprite")}catch(e){return console.error("Error al obtener sprite de",r,e),null}}export{fetchPokemonSprite};