import { pokemonHp, pokemonAttack, pokemonDefense, pokemonSAttack, pokemonSDefense, pokemonSpeed } from "../script.min.js"; function asignStats(e) { e.stats.forEach(e => { switch (e.name) { case "hp": pokemonHp.textContent = e.value; break; case "attack": pokemonAttack.textContent = e.value; break; case "defense": pokemonDefense.textContent = e.value; break; case "special-attack": pokemonSAttack.textContent = e.value; break; case "special-defense": pokemonSDefense.textContent = e.value; break; case "speed": pokemonSpeed.textContent = e.value } }) } export { asignStats };